    @Test
    public void should_serialize_correctly() throws MalformedURLException {
        Profile profile = JSON.parseObject(JSON_STRING_PROFILE, Profile.class);
        assertEquals(profile.getId(), new Long(1306029L));
        assertEquals(profile.getDisplayName(), "oui");
        assertEquals(profile.getPortrait().getUrl(), new URL("http://media.blizzard.com/sc2/portraits/0-90.jpg"));
        assertEquals(profile.getCareer().getPrimaryRace(), Race.ZERG);
        assertEquals(profile.getCareer().getHighest1v1Rank(), League.DIAMOND);
        assertEquals(profile.getSeason().getSeasonYear(), new Integer(2017));
        assertEquals(profile.getSeason().getStats().get(0).getType(), "1v1");
        assertEquals(profile.getSeason().getStats().get(0).getGames(), new Integer(130));
    }

